mapper.update:
update aecpsrvcenter10000.isv_app_association
set primary_route_rule_code=#{updatedPrimaryRouteRuleCode,jdbcType=VARCHAR}
where id=#{id,jdbcType=BIGINT} and uuid=#{uuid,jdbcType=VARCHAR}

mapper.select:
select

from aecpsrvcenter10000.isv_app_association
where 1=1

    and id=#{id,jdbcType=BIGINT}


    and gmt_create=#{gmtCreate,jdbcType=TIMESTAMP}


    and gmt_modified=#{gmtModified,jdbcType=TIMESTAMP}


    and domain_code=#{domainCode,jdbcType=VARCHAR}


    and tenant_id=#{tenantId,jdbcType=BIGINT}


    and creator=#{creator,jdbcType=VARCHAR}


    and modifier=#{modifier,jdbcType=VARCHAR}


    and is_deleted=#{isDeleted,jdbcType=CHAR}


    and mdc_ext=#{mdcExt,jdbcType=VARCHAR}


    and isv_app_unique_id=#{isvAppUniqueId,jdbcType=VARCHAR}


    and primary_route_rule_code=#{primaryRouteRuleCode,jdbcType=VARCHAR}


    and `status`=#{status,jdbcType=VARCHAR}


    and main_app_code=#{mainAppCode,jdbcType=VARCHAR}


    and isv_app_code=#{isvAppCode,jdbcType=VARCHAR}


    and uuid=#{uuid,jdbcType=VARCHAR}


    and description=#{description,jdbcType=VARCHAR}


    and main_app_unique_id=#{mainAppUniqueId,jdbcType=VARCHAR}


    and extend=#{extend,jdbcType=VARCHAR}
