mapper.select:
select id
from aecpsrvcenter10000.spi_method
where 1=1

    and interface_uuid=#{interfaceUuid,jdbcType=VARCHAR}


    and app_code=#{appCode,jdbcType=VARCHAR}

select

from aecpsrvcenter10000.spi_method
where 1=1

    and id=#{id,jdbcType=BIGINT}


    and gmt_create=#{gmtCreate,jdbcType=TIMESTAMP}


    and gmt_modified=#{gmtModified,jdbcType=TIMESTAMP}


    and domain_code=#{domainCode,jdbcType=VARCHAR}


    and tenant_id=#{tenantId,jdbcType=BIGINT}


    and creator=#{creator,jdbcType=VARCHAR}


    and modifier=#{modifier,jdbcType=VARCHAR}


    and is_deleted=#{isDeleted,jdbcType=CHAR}


    and mdc_ext=#{mdcExt,jdbcType=VARCHAR}


    and `name`=#{name,jdbcType=VARCHAR}


    and meta_data_uuid=#{metaDataUuid,jdbcType=VARCHAR}


    and method_name=#{methodName,jdbcType=VARCHAR}


    and description=#{description,jdbcType=VARCHAR}


    and in_params=#{inParams,jdbcType=VARCHAR}


    and version=#{version,jdbcType=BIGINT}


    and uuid=#{uuid,jdbcType=VARCHAR}


    and biz_type_uuid=#{bizTypeUuid,jdbcType=VARCHAR}


    and app_code=#{appCode,jdbcType=VARCHAR}


    and `status`=#{status,jdbcType=VARCHAR}


    and interface_uuid=#{interfaceUuid,jdbcType=VARCHAR}


    and change_id=#{changeId,jdbcType=VARCHAR}


    and env_uuid=#{envUuid,jdbcType=VARCHAR}


    and out_params=#{outParams,jdbcType=VARCHAR}

select count(`name`)
from aecpsrvcenter10000.spi_method
where app_code=#{appCode,jdbcType=VARCHAR}

mapper.update:
update aecpsrvcenter10000.spi_method
set `status`=#{updatedStatus,jdbcType=VARCHAR}, is_deleted='n'
where interface_uuid=#{interfaceUuid,jdbcType=VARCHAR}

    and uuid = #{uuid,jdbcType=VARCHAR}

update aecpsrvcenter10000.spi_method
set is_deleted='n',status='ON'
where interface_uuid=#{interfaceUuid,jdbcType=VARCHAR} and env_uuid=#{envUuid,jdbcType=VARCHAR}

    and change_id=#{changeId,jdbcType=VARCHAR}


    and method_name=#{methodName,jdbcType=VARCHAR}

and is_deleted='n'
